import React, { useState, useEffect, useRef } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { Link, useParams } from 'react-router-dom'
import axios from 'axios'
import io from 'socket.io-client'
export default function ChatGroupRender({Objects}){
    const socket = useRef()
    let { id } = useParams();
    const message = useRef();
    const element = useRef();
    const [allMessages, setAllMessages] = useState([])
    useEffect(() => {
        element.current.scrollTop = element.current.scrollHeight;
    })
    useEffect(() => {
        socket.current = io("ws://localhost:5000")
        socket.current.on('user-chat', (message) => {
            console.log("có tin nhắn tới")
            if(
                (
                    message.from_id._id === localStorage.getItem('AccountID')
                    &&
                    message.to_id === id
                )
                ||
                (
                    message.to_id === localStorage.getItem('AccountID')
                    &&
                    message.from_id._id === id
                )
            ){
                setAllMessages(oldArray => [...oldArray, message])
                console.log(allMessages.length)
            }
        })
        return () => {
            socket.current.disconnect()
        }
    }, [id, allMessages])
    useEffect(() => {
        const to = localStorage.getItem('AccountID')
        axios.post("http://localhost:5000/api/DMChat/GetChat", {
            user1_id: to,
            user2_id: id
        }).then(res => {
            setAllMessages(res.data)
        })
    }, [id])
    const GetName = () => {
        if(Objects.length === 0) return <></>
        return(
            <>
                {Objects.find(group => group._id === id).name}
            </>
        )
    }
    const sendMessage = () => {
        socket.current.emit('on-chat',{
            type: 'g',
            chatDate: new Date(),
            content:  message.current.value,
            from_id: localStorage.getItem('AccountID'),
            to_id: id
        })
        message.current.value = ''
    }
    //const socket = io("ws://localhost:5000")
    return(
        <div className="w-100 h-100 m-0 p-0 text-light">
            <div className="w-100 center m-0 p-0" style={{height:'15%',boxShadow:'0px 5px 5px #000316'}}>
                <div className="w-75 text-start">
                    <FontAwesomeIcon icon="fa-solid fa-user" />
                    &nbsp; <b><GetName/></b>
                </div>
            </div>
            <div className="w-100 center m-0 p-0" style={{height:'70%'}}>
                <div className="w-75 text-start">
                    
                </div>
            </div>
            <div className="w-100 center m-0 p-0" style={{height:'15%'}}>
                <div className="text-start" style={{width:'95%'}}>
                    <input className="p-1 fw-bold rounded" style={{width:'85%',background:'none', border:'0.5px solid white'}}/>
                    <button className="text-center text-light" style={{width:'15%',background:'none', border:'none'}}><FontAwesomeIcon icon="fa-solid fa-paper-plane" /></button>
                </div>
            </div>
        </div>
    )
}
