import React, { useState } from 'react'
import { Routes, Route, Navigate } from 'react-router-dom'
import Login from '../components/Login'
import Signin from '../components/Signin'
import MainBody from '../components/MainBody'
import axios from 'axios'
export default function Body() {
  const [user,setUser] = useState()
  function Checking(){
    if(localStorage.getItem('AccountID') == null){
      console.log('null localstorage')
      setUser(false)
    }else{
      axios.get(`http://localhost:5000/api/Account/getAccountWithId/${localStorage.getItem("AccountID")}`)
      .then(res => {
        if (res.data == null) {
          console.log('id not found') 
          setUser(false)
        }else{
          console.log('id found') 
          setUser(true)
        }
      })
    }
  }
  
  
  return (
    <Routes>
      {console.log(localStorage)}
      <Route path="/" element={Checking===true? <MainBody/>:<Navigate to="/login"/>}/>
      <Route path="/login" element={Checking===true? <Navigate to="/"/>:<Login/>} />
      <Route path="/signin" element={<Signin/>} />
      <Route path="*" element={<Navigate to="/"/>} />
    </Routes>
  )
}
