import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { useParams } from 'react-router-dom'
export default function ChatDMRender(Objects){
    let { id } = useParams();
    //const socket = io("ws://localhost:5000")
    const [allMessages, setAllMessages] = useState([
        {_id:'123',chatDate:'14-10-2022',content:'Xin chào, đây là là dòng chat', from_id:'123', to_id:'123'},
        {_id:'123',chatDate:'14-10-2022',content:'Xin chào, đây là là dòng chat 2', from_id:'123', to_id:'123'},
        {_id:'123',chatDate:'14-10-2022',content:'Xin chào, đây là là dòng chat 3', from_id:'123', to_id:'123'},
        {_id:'123',chatDate:'14-10-2022',content:'Xin chào, đây là là dòng chat 4', from_id:'123', to_id:'123'},
        {_id:'123',chatDate:'14-10-2022',content:'Xin chào, đây là là dòng chat 5', from_id:'123', to_id:'123'}
    ])
    const GetName = () => {
        if(Objects.Objects.length === 0) return <></>
        return(
            <b>
                {Objects.Objects.find(account => account._id === id).name}
            </b>
        )
    }
    const MessageRender = (message) => {
        return(
            <div className="w-100 m-0 p-0 bg-light text-dark">
                <div className="">
                    <div className="col-12 text-start">
                        <span>{message.message.from_id}</span> - <span>{message.message.chatDate}</span>
                    </div>
                    <div className="col-12 text-start">
                        <span>{message.message.content}</span>
                    </div>
                </div>
               
            </div>
        )
    }
    return(
        <div className="w-100 h-100 m-0 p-0 text-light">
            <div className="w-100 center m-0 p-0" style={{height:'15%',boxShadow:'0px 5px 5px #000316'}}>
                <div className="w-75 text-start">
                    <FontAwesomeIcon icon="fa-solid fa-user" />
                    &nbsp; <GetName/>
                </div>
            </div>
            <div className="w-100 center m-0 p-0" style={{height:'70%'}}>
                <div className="w-100 h-100 text-start">
                    {allMessages.map(message => <MessageRender key={message._id} message={message}/>)}
                </div>
            </div>
            <div className="w-100 center m-0 p-0" style={{height:'15%'}}>
                <div className="text-start" style={{width:'95%'}}>
                    <input className="p-1 fw-bold rounded" style={{width:'85%',background:'none', border:'0.5px solid white'}}/>
                    <button className="text-center text-light" style={{width:'15%',background:'none', border:'none'}}><FontAwesomeIcon icon="fa-solid fa-paper-plane" /></button>
                </div>
            </div>
        </div>
    )
}
