import React, { useState, useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import Moment from 'moment'
export default function ChatDMRender(Objects){
    let { id } = useParams();
    //const socket = io("ws://localhost:5000")
    const [allMessages, setAllMessages] = useState([])
    useEffect(() => {
        const to = localStorage.getItem('AccountID')
        axios.post("http://localhost:5000/api/DMChat/GetChat", {
            user1_id: to,
            user2_id: id
        }).then(res => {
            setAllMessages(res.data)
        })
    }, [])
    const GetName = () => {
        if(Objects.Objects.length === 0) return <></>
        return(
            <b>
                {Objects.Objects.find(account => account._id === id).name}
            </b>
        )
    }
    const MessageRender = (message) => {
        if(message.message)
        return(
            <div className="rounded m-0 mb-5 p-2 bg-light" style={{maxWidth:'100%',width:'max-content', height:'max-content'}}>
                <div className="text-dark" style={{maxWidth:'100%',width:'max-content', height:'max-content'}}>
                    <div className="text-start">
                        <FontAwesomeIcon icon="fa-solid fa-user" /><span className="fw-bold">{message.message.from_id.name}</span> - <span className="text-secondary">{Moment(message.message.chatDate).format('DD-MM-yyyy')}</span>
                    </div>
                    <div className="text-start" style={{maxWidth:'100%'}}>
                        <span style={{maxWidth:'100%', wordWrap:'break-word'}}>{message.message.content}</span>
                    </div>
                </div>
            </div>
        )
    }
    return(
        <div className="w-100 h-100 m-0 p-0 text-light">
            <div className="w-100 center m-0 p-0" style={{height:'15%',boxShadow:'0px 5px 5px #000316'}}>
                <div className="w-75 text-start">
                    <FontAwesomeIcon icon="fa-solid fa-user" />
                    &nbsp; <GetName/>
                </div>
            </div>
            <div className="w-100 center m-0 pt-3 pb-3 ps-2 pe-2" style={{height:'70%'}}>
                <div className="w-100 h-100 text-start" style={{overflowY:'auto'}}>
                    {allMessages.map(message => <MessageRender key={message._id} message={message}/>)}
                </div>
            </div>
            <div className="w-100 center m-0 p-0" style={{height:'15%'}}>
                <div className="text-start" style={{width:'95%'}}>
                    <input className="p-1 fw-bold rounded" style={{width:'85%',background:'none', border:'0.5px solid white'}}/>
                    <button className="text-center text-light" style={{width:'15%',background:'none', border:'none'}}><FontAwesomeIcon icon="fa-solid fa-paper-plane" /></button>
                </div>
            </div>
        </div>
    )
}
