import React, { useEffect, useRef } from 'react'
import { Routes, Route, Navigate } from 'react-router-dom'
import Login from '../components/Login'
import Signin from '../components/Signin'
import MainBody from '../components/MainBody'
import axios from 'axios'
export default function Body() {
  var user = useRef()
  useEffect(() => {
    if(localStorage.getItem('AccountID') == null){
      user.current = false
    }else{
      axios.get(`http://localhost:5000/api/Account/getAccountWithId/${localStorage.getItem("AccountID")}`).then(res => 
    {
      if (res.data == null) {
        user.current = false
      }else{
        user.current = true
      }
    })}
  }, [])
  return (
    <Routes>
      <Route path="/" element={user.current===true? <MainBody/>:<Navigate to="/login"/>}/>
      <Route path="/login" element={user.current===true? <Navigate to="/"/>:<Login/>} />
      <Route path="/signin" element={<Signin/>} />
      <Route path="*" element={<Navigate to="/"/>} />
    </Routes>
  )
}
