import React, { useEffect } from 'react'
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom'
import Login from '../components/Login'
import Signin from '../components/Signin'
import MainBody from '../components/MainBody'
import axios from 'axios'
export default function Body() {
  var user = false
  useEffect(() => {
    if(localStorage.getItem('AccountID') == null){
      user = false
      return
    }
    axios.get(`http://localhost:5000/api/Account/getAccountWithId/${localStorage.getItem("AccountID")}`).then(res => 
    {
      if (res.data == null) {
        user = false
        return
      }
      user = true
    })
  }, [])
  return (
    <Routes>
      <Route path="/" element={user===true? <MainBody/>:<Navigate to="/login"/>}/>
      <Route path="/login" element={user===true? <Navigate to="/"/>:<Login/>} />
      <Route path="/signin" element={<Signin/>} />
      
    </Routes>
  )
}
