import React, { useEffect, useRef } from 'react'
import { Routes, Route, Navigate } from 'react-router-dom'
import Login from '../components/Login'
import Signin from '../components/Signin'
import MainBody from '../components/MainBody'
import axios from 'axios'
export default function Body() {
  var user
  const checking = () => {
    if(localStorage.getItem('AccountID') == null){
      user = false
      console.log('null localstorage')
    }else{
      axios.get(`http://localhost:5000/api/Account/getAccountWithId/${localStorage.getItem("AccountID")}`)
      .then(res => {
        if (res.data == null) {
          user = false
          console.log('id not found')
        }else{
          user = true
          console.log('id found')
        }
      })
    }
  }
  
  return (
    <Routes>
      {console.log(`render: ${user}`)}
      <Route path="/" element={user===true? <MainBody/>:<Navigate to="/login"/>}/>
      <Route path="/login" element={user===true? <Navigate to="/"/>:<Login/>} />
      <Route path="/signin" element={<Signin/>} />
      <Route path="*" element={<Navigate to="/"/>} />
    </Routes>
  )
}
