import React, { useEffect } from 'react'
import { Routes, Route, Navigate, useNavigate } from 'react-router-dom'
import Login from '../components/Login'
import Signin from '../components/Signin'
import MainBody from '../components/MainBody'
import axios from 'axios'
export default function Body() {
  useEffect({
    
  })
  const GetAccount = () => {
    if(localStorage.getItem('AccountID') == null) return false
    axios.get(`http://localhost:5000/api/Account/getAccountWithId/${localStorage.getItem("AccountID")}`).then(res => 
    {
      if (res.data == null) {
        return false
      }
      return true
    })
  }
  return (
    <Routes>
      <Route path="*" element={GetAccount? <MainBody/>:<Navigate to="/Login"/>}/>
      <Route path="/login" element={GetAccount===false? <Navigate to="/"/>:<Login/>} />
      <Route path="/signin" element={<Signin/>} />
    </Routes>
  )
}
