import React,{ useEffect } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { Link, useParams } from 'react-router-dom'
import { io } from 'socket.io-client'


useEffect(() => {
    axios.get("http://localhost:5000/api/Account/getAllAccount").then(res => {
        setAllAccounts(res.data)
    })
    axios.get("http://localhost:5000/api/Group/getAllGroup").then(res => {
        setAllGroups(res.data)
    })
    console.log(localStorage.getItem('AccountID'))
}, []);
export const ChatUserRender = (object) => {
    const link = `/message/dm/${object.object._id}`;
    return(
        <Link to={link} className="w-100 m-0 p-0 row dotText text-light" style={{height:'50px', textDecoration:'none'}}>
            <div className="col-3 m-auto p-0 center">
                <FontAwesomeIcon icon="fa-solid fa-user" />
            </div>
            <div className="col-9 text-start p-0 m-auto fs-5 pr-2">
                {object.object.name}
            </div>
        </Link>
    )
}
export const GroupUserRender = (object) => {
    const link = `/message/g/${object.object._id}`;
    return(
        <Link to={link} className="w-100 m-0 p-0 row dotText text-light" style={{height:'50px', textDecoration:'none'}}>
            <div className="col-3 m-auto p-0 center">
                <FontAwesomeIcon icon="fa-solid fa-user" />
            </div>
            <div className="col-8 text-start p-0 m-auto fs-5">
                {object.object.name}
            </div>
        </Link>
    )
}


export const ChatDMRender = () => {
    let { id } = useParams();
    const socket = io("ws://localhost:5000")
    const name = allAccounts.find(account => account._id === id).name
    return(
        <div className="w-100 h-100 m-0 p-0 text-light">
            <div className="w-100 center m-0 p-0" style={{height:'15%',boxShadow:'0px 5px 7px #000316'}}>
                <div className="w-75 text-start">
                    <FontAwesomeIcon icon="fa-solid fa-user" />
                    &nbsp; <b>{name}</b>
                </div>
            </div>
            
        </div>
    )
}

export const ChatGroupRender = () => {
    let { id } = useParams();
    const socket = io("ws://localhost:5000")
    const name = allGroups.find(group => group._id === id).name
    return(
        <div className="w-100 h-100 m-0 p-0 text-light">
            <div className="w-100 center m-0 p-0" style={{height:'15%',boxShadow:'0px 5px 7px #000316'}}>
                <div className="w-75 text-start">
                    <FontAwesomeIcon icon="fa-solid fa-user-group" />
                    &nbsp; <b>{name}</b>
                </div>
            </div>
        </div>
    )
}
